    protected String setAbstractGenerateInsertExcelSXLSX(CerCertificadoFirma bean) {
        String fechaS = "";
        String sqlInsert = "INSERT INTO cer_certificado_firma(";
        sqlInsert = sqlInsert + "cer_certificado_firma_id" + ", ";
        sqlInsert = sqlInsert + "descripcion" + ", ";
        sqlInsert = sqlInsert + "fecha_aprobacion" + ", ";
        sqlInsert = sqlInsert + "ind_aprobacion_rechazo" + ", ";
        sqlInsert = sqlInsert + "cer_firma_id" + ", ";
        sqlInsert = sqlInsert + "cer_certificado_id" + ")";
        sqlInsert = sqlInsert + " VALUES (";
        sqlInsert = sqlInsert + bean.getId() + ", ";
        if (StringUtils.isBlank(bean.getDescripcion())) {
            sqlInsert = sqlInsert + "null" + ", ";
        } else {
            sqlInsert = sqlInsert + "'" + bean.getDescripcion() + "'" + ", ";
        }
        if (bean.getFechaAprobacion() != null) {
            fechaS = DateUtils.convertDateToString("dd/MM/yyyy HH:mm:ss", bean.getFechaAprobacion());
            sqlInsert = sqlInsert + "to_date('" + fechaS + "','dd/MM/yyyy HH:mm:ss')" + ", ";
        } else {
            sqlInsert = sqlInsert + "null" + ", ";
        }
        if (StringUtils.isBlank(bean.getIndAprobacionRechazo())) {
            sqlInsert = sqlInsert + "null" + ", ";
        } else {
            sqlInsert = sqlInsert + "'" + bean.getIndAprobacionRechazo() + "'" + ", ";
        }
        sqlInsert = sqlInsert + bean.getCerCertificado().getId() + ", ";
        sqlInsert = sqlInsert + bean.getCerFirma().getId();
        sqlInsert = sqlInsert + " );";
        return sqlInsert;
    }

