    protected Horario setUploadExcel(Cell currentCell, Horario horario, int contador) throws Exception {
        Double valorDouble = new Double(0);
        BigDecimal valorDecimal = new BigDecimal(0);
        Boolean valorBoolean = new Boolean(true);
        String valorCadena = "";
        switch (contador) {
        case 1:
            try {
                valorCadena = currentCell.getStringCellValue();
                Date valorFechaD = DateUtils.convertStringToDate("dd/MM/yyyy", valorCadena);
                horario.setFechaInicio(valorFechaD);
            } catch (Exception ex) {
                throw new ServiceException("Valor Campo fechaInicio está en formato incorrecto");
            }
            break;
        case 2:
            try {
                valorCadena = currentCell.getStringCellValue();
                if (valorCadena.length() > 5) {
                    throw new ServiceException("Valor Campo horaInicio contiene mas de 5 caracter(es)");
                }
                horario.setHoraInicio(valorCadena);
            } catch (Exception e) {
                throw new ServiceException("Valor Campo horaInicio está en formato incorrecto");
            }
            break;
        case 3:
            try {
                valorCadena = currentCell.getStringCellValue();
                if (valorCadena.length() > 5) {
                    throw new ServiceException("Valor Campo horaFin contiene mas de 5 caracter(es)");
                }
                horario.setHoraFin(valorCadena);
            } catch (Exception e) {
                throw new ServiceException("Valor Campo horaFin está en formato incorrecto");
            }
            break;
        case 4:
            try {
                valorCadena = currentCell.getStringCellValue();
                if (valorCadena.length() > 20) {
                    throw new ServiceException("Valor Campo frecuencia contiene mas de 20 caracter(es)");
                }
                horario.setFrecuencia(valorCadena);
            } catch (Exception e) {
                throw new ServiceException("Valor Campo frecuencia está en formato incorrecto");
            }
            break;
        case 5:
            try {
                valorCadena = currentCell.getStringCellValue();
                if (valorCadena.length() > 20) {
                    throw new ServiceException("Valor Campo modalidad contiene mas de 20 caracter(es)");
                }
                horario.setModalidad(valorCadena);
            } catch (Exception e) {
                throw new ServiceException("Valor Campo modalidad está en formato incorrecto");
            }
            break;
        default:
            break;
        }
        return horario;
    }

