    protected String setAbstractGenerateInsertExcelSXLSX(Horario bean) {
        String fechaS = "";
        String sqlInsert = "INSERT INTO HORARIO(";
        sqlInsert = sqlInsert + "HORARIO_ID" + ", ";
        sqlInsert = sqlInsert + "FECHA_INICIO" + ", ";
        sqlInsert = sqlInsert + "HORA_INICIO" + ", ";
        sqlInsert = sqlInsert + "HORA_FIN" + ", ";
        sqlInsert = sqlInsert + "FRECUENCIA" + ", ";
        sqlInsert = sqlInsert + "MODALIDAD" + ", ";
        sqlInsert = sqlInsert + "CURSO_ID" + ")";
        sqlInsert = sqlInsert + " VALUES (";
        sqlInsert = sqlInsert + bean.getId() + ", ";
        if (bean.getFechaInicio() != null) {
            fechaS = DateUtils.convertDateToString("dd/MM/yyyy HH:mm:ss", bean.getFechaInicio());
            sqlInsert = sqlInsert + "to_date('" + fechaS + "','dd/MM/yyyy HH:mm:ss')" + ", ";
        } else {
            sqlInsert = sqlInsert + "null" + ", ";
        }
        if (StringUtils.isBlank(bean.getHoraInicio())) {
            sqlInsert = sqlInsert + "null" + ", ";
        } else {
            sqlInsert = sqlInsert + "'" + bean.getHoraInicio() + "'" + ", ";
        }
        if (StringUtils.isBlank(bean.getHoraFin())) {
            sqlInsert = sqlInsert + "null" + ", ";
        } else {
            sqlInsert = sqlInsert + "'" + bean.getHoraFin() + "'" + ", ";
        }
        if (StringUtils.isBlank(bean.getFrecuencia())) {
            sqlInsert = sqlInsert + "null" + ", ";
        } else {
            sqlInsert = sqlInsert + "'" + bean.getFrecuencia() + "'" + ", ";
        }
        if (StringUtils.isBlank(bean.getModalidad())) {
            sqlInsert = sqlInsert + "null" + ", ";
        } else {
            sqlInsert = sqlInsert + "'" + bean.getModalidad() + "'" + ", ";
        }
        sqlInsert = sqlInsert + bean.getCurso().getId();
        sqlInsert = sqlInsert + " );";
        return sqlInsert;
    }

