mapper.select:
	SELECT 
      PARAMETRIA_ID , 
      DESCRIPTION , 
      LABEL , 
      MODULO , 
      STATUS , 
      VALUE_1 , 
      VALUE_2 , 
      VALUE_3 
	FROM
	APP_PARAMETRIA
	WHERE 1 = 1

		AND PARAMETRIA_ID = #{id, jdbcType=INTEGER}


		AND DESCRIPTION = #{description}


		AND LABEL = #{label}


		AND MODULO = #{modulo}


		AND STATUS = #{status}


		AND VALUE_1 = #{value1}


		AND VALUE_2 = #{value2}


		AND VALUE_3 = #{value3}

mapper.insert:
INSERT INTO APP_PARAMETRIA( PARAMETRIA_ID , DESCRIPTION , LABEL , MODULO , STATUS , VALUE_1 , VALUE_2 , VALUE_3 ) (
SELECT #{element.id} , #{element.description} , #{element.label} , #{element.modulo} , #{element.status} , #{element.value1} , #{element.value2} , #{element.value3} from dual
)

mapper.delete:
    DELETE FROM APP_PARAMETRIA
	WHERE 1 = 1

		AND PARAMETRIA_ID = #{id, jdbcType=INTEGER}


		AND DESCRIPTION = #{description}


		AND LABEL = #{label}


		AND MODULO = #{modulo}


		AND STATUS = #{status}


		AND VALUE_1 = #{value1}


		AND VALUE_2 = #{value2}


		AND VALUE_3 = #{value3}
