mapper.select:
	SELECT 
      parametria_id , 
      modulo , 
      label , 
      description , 
      value_1 , 
      value_2 , 
      value_3 , 
      status 
	FROM
	app_parametria
	WHERE 1 = 1

		AND parametria_id = #{id, jdbcType=INTEGER}


		AND modulo = #{modulo}


		AND label = #{label}


		AND description = #{description}


		AND value_1 = #{value1}


		AND value_2 = #{value2}


		AND value_3 = #{value3}


		AND status = #{status}

mapper.insert:
INSERT INTO app_parametria( parametria_id , modulo , label , description , value_1 , value_2 , value_3 , status ) (
SELECT #{element.id} , #{element.modulo} , #{element.label} , #{element.description} , #{element.value1} , #{element.value2} , #{element.value3} , #{element.status} from dual
)

mapper.delete:
    DELETE FROM app_parametria
	WHERE 1 = 1

		AND parametria_id = #{id, jdbcType=INTEGER}


		AND modulo = #{modulo}


		AND label = #{label}


		AND description = #{description}


		AND value_1 = #{value1}


		AND value_2 = #{value2}


		AND value_3 = #{value3}


		AND status = #{status}
