mapper.select:
	SELECT 
      app_proceso_log_id , 
      fecha_inicio_ejecucion , 
      fecha_fin_ejecucion , 
      estado_ejecucion , 
      descripcion_estado_ejecucion , 
      modulo , 
      clase_programacion , 
      metodo_programacion , 
      parametro_entrada , 
      resultado_salida , 
      duracion_ms 
	FROM
	app_proceso_log
	WHERE 1 = 1

		AND app_proceso_log_id = #{id, jdbcType=INTEGER}


		AND fecha_inicio_ejecucion = #{fechaInicioEjecucion, jdbcType=DATE}


		AND fecha_fin_ejecucion = #{fechaFinEjecucion, jdbcType=DATE}


		AND estado_ejecucion = #{estadoEjecucion}


		AND descripcion_estado_ejecucion = #{descripcionEstadoEjecucion}


		AND modulo = #{modulo}


		AND clase_programacion = #{claseProgramacion}


		AND metodo_programacion = #{metodoProgramacion}


		AND parametro_entrada = #{parametroEntrada}


		AND resultado_salida = #{resultadoSalida}


		AND duracion_ms = #{duracionMs, jdbcType=INTEGER}

mapper.insert:
INSERT INTO app_proceso_log( app_proceso_log_id , fecha_inicio_ejecucion , fecha_fin_ejecucion , estado_ejecucion , descripcion_estado_ejecucion , modulo , clase_programacion , metodo_programacion , parametro_entrada , resultado_salida , duracion_ms ) (
SELECT #{element.id} , #{element.fechaInicioEjecucion} , #{element.fechaFinEjecucion} , #{element.estadoEjecucion} , #{element.descripcionEstadoEjecucion} , #{element.modulo} , #{element.claseProgramacion} , #{element.metodoProgramacion} , #{element.parametroEntrada} , #{element.resultadoSalida} , #{element.duracionMs} from dual
)

mapper.delete:
    DELETE FROM app_proceso_log
	WHERE 1 = 1

		AND app_proceso_log_id = #{id, jdbcType=INTEGER}


		AND fecha_inicio_ejecucion = #{fechaInicioEjecucion, jdbcType=DATE}


		AND fecha_fin_ejecucion = #{fechaFinEjecucion, jdbcType=DATE}


		AND estado_ejecucion = #{estadoEjecucion}


		AND descripcion_estado_ejecucion = #{descripcionEstadoEjecucion}


		AND modulo = #{modulo}


		AND clase_programacion = #{claseProgramacion}


		AND metodo_programacion = #{metodoProgramacion}


		AND parametro_entrada = #{parametroEntrada}


		AND resultado_salida = #{resultadoSalida}


		AND duracion_ms = #{duracionMs, jdbcType=INTEGER}
