mapper.select:
	SELECT 
    cerd.cer_certificado_detalle_id ,
	cerd.cer_certificado_id ,
	cerd.cer_nota_pedido_detalle_id ,
	cerd.subpackno ,
	cerd.rango ,
	cerd.num_orden ,
	cerd.nivel_estructura ,
	cerd.indicador_item_adici ,
	cerd.cta_contable ,
	cerd.precio_unitario ,
	cerd.descuento ,
	cerd.cantidad_total ,
	cerd.cantidad_pendiente ,
	cerd.cantidad_aprobada ,
	cerd.total_linea ,
	cerd.total_linea_ajustado ,
	cerd.asign_servicio ,
	cerd.auxiliar ,
	cerd.mtr_cuenta_imputacion_id ,
	cerd.mtr_cuenta_mayor_id ,
	cerd.fecha_creacion_migo ,
	impu.codigo_cuenta_imputacion,
	impu.descripcion descripcionImpu,
	mayor.codigo_cuenta_mayor,
	mayor.descripcion descripcionMayor
	FROM
	cer_certificado_detalle cerd
	LEFT JOIN mtr_cuenta_imputacion impu on (impu.mtr_cuenta_imputacion_id = cerd.mtr_cuenta_imputacion_id)
	LEFT JOIN mtr_cuenta_mayor mayor on (mayor.mtr_cuenta_mayor_id = cerd.mtr_cuenta_mayor_id)
	WHERE 1 = 1

		AND cerd.cer_certificado_detalle_id = #{id, jdbcType=INTEGER}


		AND cerd.cer_certificado_id = #{cerCertificado.id, jdbcType=INTEGER}


		AND cerd.cer_nota_pedido_detalle_id = #{cerNotaPedidoDetalle.id, jdbcType=INTEGER}


		AND cerd.subpackno = #{subpackno}


		AND cerd.rango = #{rango}


		AND cerd.num_orden = #{numOrden}


		AND cerd.nivel_estructura = #{nivelEstructura}


		AND cerd.indicador_item_adici = #{indicadorItemAdici}


		AND cerd.cta_contable = #{ctaContable}


		AND cerd.precio_unitario = #{precioUnitario, jdbcType=NUMERIC}


		AND cerd.descuento = #{descuento, jdbcType=NUMERIC}


		AND cerd.cantidad_total = #{cantidadTotal, jdbcType=NUMERIC}


		AND cerd.cantidad_pendiente = #{cantidadPendiente, jdbcType=NUMERIC}


		AND cerd.cantidad_aprobada = #{cantidadAprobada, jdbcType=NUMERIC}


		AND cerd.total_linea = #{totalLinea, jdbcType=NUMERIC}


		AND cerd.total_linea_ajustado = #{totalLineaAjustado, jdbcType=NUMERIC}


		AND cerd.asign_servicio = #{asignServicio}


		AND cerd.auxiliar = #{auxiliar}


		AND cerd.mtr_cuenta_imputacion_id = #{mtrCuentaImputacion.id, jdbcType=INTEGER}


		AND cerd.mtr_cuenta_mayor_id = #{mtrCuentaMayor.id, jdbcType=INTEGER}


		AND cerd.fecha_creacion_migo = #{fechaCreacionMigo, jdbcType=DATE}

mapper.insert:
INSERT INTO cer_certificado_detalle( cer_certificado_detalle_id , cer_certificado_id , cer_nota_pedido_detalle_id , subpackno , rango , num_orden , nivel_estructura , indicador_item_adici , cta_contable , precio_unitario , descuento , cantidad_total , cantidad_pendiente , cantidad_aprobada , total_linea , total_linea_ajustado , asign_servicio , auxiliar , mtr_cuenta_imputacion_id , mtr_cuenta_mayor_id , fecha_creacion_migo ) (
SELECT #{element.id} , #{element.cerCertificado.id} , #{element.cerNotaPedidoDetalle.id} , #{element.subpackno} , #{element.rango} , #{element.numOrden} , #{element.nivelEstructura} , #{element.indicadorItemAdici} , #{element.ctaContable} , #{element.precioUnitario} , #{element.descuento} , #{element.cantidadTotal} , #{element.cantidadPendiente} , #{element.cantidadAprobada} , #{element.totalLinea} , #{element.totalLineaAjustado} , #{element.asignServicio} , #{element.auxiliar} , #{element.mtrCuentaImputacion.id} , #{element.mtrCuentaMayor.id} , #{element.fechaCreacionMigo} from dual
)

mapper.delete:
    DELETE FROM cer_certificado_detalle
	WHERE 1 = 1

		AND cer_certificado_detalle_id = #{id, jdbcType=INTEGER}


		AND cer_certificado_id = #{cerCertificado.id}


		AND cer_nota_pedido_detalle_id = #{cerNotaPedidoDetalle.id}


		AND subpackno = #{subpackno}


		AND rango = #{rango}


		AND num_orden = #{numOrden}


		AND nivel_estructura = #{nivelEstructura}


		AND indicador_item_adici = #{indicadorItemAdici}


		AND cta_contable = #{ctaContable}


		AND precio_unitario = #{precioUnitario, jdbcType=NUMERIC}


		AND descuento = #{descuento, jdbcType=NUMERIC}


		AND cantidad_total = #{cantidadTotal, jdbcType=NUMERIC}


		AND cantidad_pendiente = #{cantidadPendiente, jdbcType=NUMERIC}


		AND cantidad_aprobada = #{cantidadAprobada, jdbcType=NUMERIC}


		AND total_linea = #{totalLinea, jdbcType=NUMERIC}


		AND total_linea_ajustado = #{totalLineaAjustado, jdbcType=NUMERIC}


		AND asign_servicio = #{asignServicio}


		AND auxiliar = #{auxiliar}


		AND mtr_cuenta_imputacion_id = #{mtrCuentaImputacion.id}


		AND mtr_cuenta_mayor_id = #{mtrCuentaMayor.id}


		AND fecha_creacion_migo = #{fechaCreacionMigo, jdbcType=DATE}
