mapper.select:
SELECT
cer_certificado_detalle_sap_id ,
cer_certificado_detalle_id ,
documento_generado ,
texto_error
FROM
cer_certificado_detalle_sap
WHERE 1 = 1

	AND cer_certificado_detalle_sap_id = #{id, jdbcType=INTEGER}


	AND cer_certificado_detalle_id = #{cerCertificadoDetalle.id, jdbcType=INTEGER}


	AND documento_generado = #{documentoGenerado}


	AND texto_error = #{textoError}

/*select a.CER_CERTIFICADO_DETALLE_SAP_ID, a.CREATED_BY, a.CREATED_DATE, a.MODIFIED_BY, a.MODIFIED_DATE, a.DOCUMENTO_GENERADO, a.POSICION_SAP, a.TEXTO_ERROR, a.CER_CERTIFICADO_DETALLE_ID from IPROVIDER_DEV.CER_CERTIFICADO_DETALLE_SAP a inner join CER_CERTIFICADO_DETALLE b on a.CER_CERTIFICADO_DETALLE_ID = b.CER_CERTIFICADO_DETALLE_ID inner join CER_CERTIFICADO c on c.CER_CERTIFICADO_ID = b.CER_CERTIFICADO_ID where 1 = 1
AND c.CER_CERTIFICADO_ID = idCertificado, jdbcType=INTEGER */
select a.CER_CERTIFICADO_DETALLE_SAP_ID, a.CREATED_BY, a.CREATED_DATE, a.MODIFIED_BY, a.MODIFIED_DATE, a.DOCUMENTO_GENERADO, a.POSICION_SAP, a.TEXTO_ERROR, a.CER_CERTIFICADO_DETALLE_ID from IPROVIDER_DEV.CER_CERTIFICADO_DETALLE_SAP a inner join CER_CERTIFICADO_DETALLE b on a.CER_CERTIFICADO_DETALLE_ID = b.CER_CERTIFICADO_DETALLE_ID where 1 = 1
AND a.CER_CERTIFICADO_DETALLE_ID = #{idDetalleCertificado, jdbcType=INTEGER}

SELECT cer_certificado_detalle_sap_id , cer_certificado_detalle_id , documento_generado , texto_error FROM cer_certificado_detalle_sap WHERE 1 = 1 and documento_generado is not null and length(documento_generado) > 0
AND cer_certificado_detalle_id = #{idDetalleCertificado, jdbcType=INTEGER}
AND posicion_sap = #{posicionSap}

SELECT a.cer_certificado_detalle_sap_id , a.cer_certificado_detalle_id , a.documento_generado , a.texto_error FROM cer_certificado_detalle_sap a inner join cer_certificado_detalle b on a.cer_certificado_detalle_id = b.cer_certificado_detalle_id inner join cer_nota_pedido_detalle c on b.cer_nota_pedido_detalle_id = c.cer_nota_pedido_detalle_id WHERE 1 = 1 and documento_generado is not null and length(documento_generado) > 0
AND c.id_padre = #{idPadre, jdbcType=INTEGER}
AND b.cer_certificado_id = #{idCertificado, jdbcType=INTEGER}
order by a.documento_generado asc

select a.cer_certificado_detalle_sap_id, a.documento_generado, a.posicion_sap, a.texto_error, a.cer_certificado_detalle_id from cer_certificado_detalle_sap as a inner join cer_certificado_detalle as b on a.cer_certificado_detalle_id = b.cer_certificado_detalle_id where 1 = 1
AND b.cer_certificado_id = #{idDetalleCertificado, jdbcType=INTEGER}
order by a.cer_certificado_detalle_sap_id desc

mapper.insert:
INSERT INTO cer_certificado_detalle_sap( cer_certificado_detalle_sap_id , cer_certificado_detalle_id , documento_generado , texto_error ) (
SELECT #{element.id} , #{element.cerCertificadoDetalle.id} , #{element.documentoGenerado} , #{element.textoError} from dual
)

mapper.delete:
DELETE FROM cer_certificado_detalle_sap
WHERE 1 = 1

	AND cer_certificado_detalle_sap_id = #{id, jdbcType=INTEGER}


	AND cer_certificado_detalle_id = #{cerCertificadoDetalle.id}


	AND documento_generado = #{documentoGenerado}


	AND texto_error = #{textoError}
