mapper.select:
	SELECT 
      cer_certificado_firma_id , 
      cer_firma_id , 
      cer_certificado_id , 
      descripcion , 
      fecha_aprobacion , 
      ind_aprobacion_rechazo 
	FROM
	cer_certificado_firma
	WHERE 1 = 1

		AND cer_certificado_firma_id = #{id, jdbcType=INTEGER}


		AND cer_firma_id = #{cerFirma.id, jdbcType=INTEGER}


		AND cer_certificado_id = #{cerCertificado.id, jdbcType=INTEGER}


		AND descripcion = #{descripcion}


		AND fecha_aprobacion = #{fechaAprobacion, jdbcType=DATE}


		AND ind_aprobacion_rechazo = #{indAprobacionRechazo}

mapper.insert:
INSERT INTO cer_certificado_firma( cer_certificado_firma_id , cer_firma_id , cer_certificado_id , descripcion , fecha_aprobacion , ind_aprobacion_rechazo ) (
SELECT #{element.id} , #{element.cerFirma.id} , #{element.cerCertificado.id} , #{element.descripcion} , #{element.fechaAprobacion} , #{element.indAprobacionRechazo} from dual
)

mapper.delete:
    DELETE FROM cer_certificado_firma
	WHERE 1 = 1

		AND cer_certificado_firma_id = #{id, jdbcType=INTEGER}


		AND cer_firma_id = #{cerFirma.id}


		AND cer_certificado_id = #{cerCertificado.id}


		AND descripcion = #{descripcion}


		AND fecha_aprobacion = #{fechaAprobacion, jdbcType=DATE}


		AND ind_aprobacion_rechazo = #{indAprobacionRechazo}
