mapper.select:
SELECT
cer.cer_certificado_id ,
cer.codigo_certificado ,
cer.mtr_proveedor_id ,
prov.ruc rucProveedor,
prov.razon_social razonSocialProveedor,
prov.direccion direccionProveedor,
cer.cer_nota_pedido_id ,
cer.mtr_sociedad_id ,
soc.desc_brv descripcionSocBreve,
soc.descripcion descripcionSoc,
soc.estado estadoSoc,
soc.ruc rucSoc,
cer.mtr_estado_id ,
estado.codigo_estado,
estado.descripcion descripcionEstado,
cer.mtr_moneda_id ,
mone.sigla siglaMone,
mone.moneda_descrip descripcionMone,
mone.desc_brv descripcionBreveMone,
cer.mtr_clase_documento_id ,
clase.codigo_sap codigoSapClase,
clase.descripcion descripcionClase,
cer.concepto ,
cer.descuentoh ,
cer.fecha_aprobacion ,
cer.fecha_contab_document ,
cer.fecha_desde ,
cer.fecha_hasta ,
cer.grupo_compras ,
cer.hoja_servicio ,
cer.lugar_prest_serv ,
cer.monto ,
cer.monto_total ,
cer.monto_total_adjustado ,
cer.notas_rechazo ,
cer.observacion ,
cer.ocurrencia ,
cer.respo_externo ,
cer.solicitante ,
cer.ind_tiene_factura ,
cer.mtr_centro_id ,
cer.mtr_almacen_id ,
cer.ind_creacion_automatica ,
cer.fecha_contabilizacion ,
cer.nro_guia_remision,

nota.codigo_nota_pedido_sap codigoNotaPedidoSap,
nota.tipo tipoNotaPedido,
estadoNota.codigo_estado codigoEstadoNota,
estadoNota.descripcion descripcionEstadoNota

FROM
cer_certificado cer
LEFT JOIN cer_nota_pedido nota ON (nota.cer_nota_pedido_id = cer.cer_nota_pedido_id)
LEFT JOIN mtr_clase_documento clase on (clase.mtr_clase_documento_id = cer.mtr_clase_documento_id)
LEFT JOIN mtr_moneda mone on (mone.mtr_moneda_id = cer.mtr_moneda_id)
LEFT JOIN mtr_estado estado on (estado.mtr_estado_id = cer.mtr_estado_id)
LEFT JOIN mtr_sociedad soc on (soc.mtr_sociedad_id = cer.mtr_sociedad_id)
LEFT JOIN mtr_proveedor prov on (prov.mtr_proveedor_id = cer.mtr_proveedor_id)
LEFT JOIN mtr_estado estadoNota on (estadoNota.mtr_estado_id = nota.mtr_estado_id)
WHERE 1 = 1

	AND cer.cer_certificado_id = #{id, jdbcType=INTEGER}


	AND cer.codigo_certificado = #{codigoCertificado}


	AND cer.mtr_proveedor_id = #{mtrProveedor.id, jdbcType=INTEGER}


	AND cer.cer_nota_pedido_id = #{cerNotaPedido.id, jdbcType=INTEGER}


	AND cer.mtr_sociedad_id = #{mtrSociedad.id, jdbcType=INTEGER}


	AND cer.mtr_estado_id = #{mtrEstado.id, jdbcType=INTEGER}


	AND cer.mtr_moneda_id = #{mtrMoneda.id, jdbcType=INTEGER}


	AND cer.mtr_clase_documento_id = #{mtrClaseDocumento.id, jdbcType=INTEGER}


	AND cer.concepto = #{concepto}


	AND cer.descuentoh = #{descuentoh}


	AND cer.fecha_aprobacion = #{fechaAprobacion, jdbcType=DATE}


	AND cer.fecha_contab_document = #{fechaContabDocument, jdbcType=DATE}


	AND cer.fecha_desde = #{fechaDesde, jdbcType=DATE}


	AND cer.fecha_hasta = #{fechaHasta, jdbcType=DATE}


	AND cer.grupo_compras = #{grupoCompras}


	AND cer.hoja_servicio = #{hojaServicio}


	AND cer.lugar_prest_serv = #{lugarPrestServ}


	AND cer.monto = #{monto, jdbcType=NUMERIC}


	AND cer.monto_total = #{montoTotal, jdbcType=NUMERIC}


	AND cer.monto_total_adjustado = #{montoTotalAdjustado, jdbcType=NUMERIC}


	AND cer.notas_rechazo = #{notasRechazo}


	AND cer.observacion = #{observacion}


	AND cer.ocurrencia = #{ocurrencia, jdbcType=INTEGER}


	AND cer.respo_externo = #{respoExterno}


	AND cer.solicitante = #{solicitante}


	AND cer.ind_tiene_factura = #{indTieneFactura}


	AND cer.mtr_centro_id = #{mtrCentro.id, jdbcType=INTEGER}


	AND cer.mtr_almacen_id = #{mtrAlmacen.id, jdbcType=INTEGER}


	AND cer.ind_creacion_automatica = #{indCreacionAutomatica}


	AND cer.fecha_contabilizacion = #{fechaContabilizacion, jdbcType=DATE}


	AND cer.nro_guia_remision = #{nroGuiaRemision}

mapper.insert:
INSERT INTO cer_certificado( cer_certificado_id , codigo_certificado , mtr_proveedor_id , cer_nota_pedido_id , mtr_sociedad_id , mtr_estado_id , mtr_moneda_id , mtr_clase_documento_id , concepto , descuentoh , fecha_aprobacion , fecha_contab_document , fecha_desde , fecha_hasta , grupo_compras , hoja_servicio , lugar_prest_serv , monto , monto_total , monto_total_adjustado , notas_rechazo , observacion , ocurrencia , respo_externo , solicitante , ind_tiene_factura , mtr_centro_id , mtr_almacen_id , ind_creacion_automatica , fecha_contabilizacion , nro_guia_remision ) (
SELECT #{element.id} , #{element.codigoCertificado} , #{element.mtrProveedor.id} , #{element.cerNotaPedido.id} , #{element.mtrSociedad.id} , #{element.mtrEstado.id} , #{element.mtrMoneda.id} , #{element.mtrClaseDocumento.id} , #{element.concepto} , #{element.descuentoh} , #{element.fechaAprobacion} , #{element.fechaContabDocument} , #{element.fechaDesde} , #{element.fechaHasta} , #{element.grupoCompras} , #{element.hojaServicio} , #{element.lugarPrestServ} , #{element.monto} , #{element.montoTotal} , #{element.montoTotalAdjustado} , #{element.notasRechazo} , #{element.observacion} , #{element.ocurrencia} , #{element.respoExterno} , #{element.solicitante} , #{element.indTieneFactura} , #{element.mtrCentro.id} , #{element.mtrAlmacen.id} , #{element.indCreacionAutomatica} , #{element.fechaContabilizacion} , #{element.nroGuiaRemision} from dual
)

mapper.delete:
DELETE FROM cer_certificado
WHERE 1 = 1

	AND cer_certificado_id = #{id, jdbcType=INTEGER}


