mapper.select:
SELECT fir.cer_firma_id , fir.cer_nota_pedido_id , fir.mtr_tipo_firma_id , fir.mtr_aprobador_id , fir.ebeln , apro.aprobador_sap, apro.nombre, apro.apellidos, apro.email, apro.estado, apro.codigo_idp, tipo.codigo_tipo_firma, tipo.descripcion descripcionFirma FROM cer_firma fir LEFT JOIN mtr_aprobador apro on (apro.mtr_aprobador_id = fir.mtr_aprobador_id) LEFT JOIN mtr_tipo_firma tipo on (tipo.mtr_tipo_firma_id = fir.mtr_tipo_firma_id) WHERE 1 = 1
AND fir.cer_firma_id = #{id, jdbcType=INTEGER}
AND fir.cer_nota_pedido_id = #{cerNotaPedido.id, jdbcType=INTEGER}
AND fir.mtr_tipo_firma_id = #{mtrTipoFirma.id, jdbcType=INTEGER}
AND fir.mtr_aprobador_id = #{mtrAprobador.id, jdbcType=INTEGER}
AND fir.ebeln = #{ebeln}

mapper.insert:
INSERT INTO cer_firma( cer_firma_id , cer_nota_pedido_id , mtr_tipo_firma_id , mtr_aprobador_id , ebeln ) (
SELECT #{element.id} , #{element.cerNotaPedido.id} , #{element.mtrTipoFirma.id} , #{element.mtrAprobador.id} , #{element.ebeln} from dual
)

mapper.delete:
    DELETE FROM cer_firma
	WHERE 1 = 1

		AND cer_firma_id = #{id, jdbcType=INTEGER}


		AND cer_nota_pedido_id = #{cerNotaPedido.id}


		AND mtr_tipo_firma_id = #{mtrTipoFirma.id}


		AND mtr_aprobador_id = #{mtrAprobador.id}


		AND ebeln = #{ebeln}
