mapper.select:
	SELECT 
      cer_historial_detalle_sap_id , 
      cer_historial_id , 
      descripcion , 
      posicion_sap 
	FROM
	cer_historial_detalle_sap
	WHERE 1 = 1

		AND cer_historial_detalle_sap_id = #{id, jdbcType=INTEGER}


		AND cer_historial_id = #{cerHistorial.id, jdbcType=INTEGER}


		AND descripcion = #{descripcion}


		AND posicion_sap = #{posicionSap}

mapper.insert:
INSERT INTO cer_historial_detalle_sap( cer_historial_detalle_sap_id , cer_historial_id , descripcion , posicion_sap ) (
SELECT #{element.id} , #{element.cerHistorial.id} , #{element.descripcion} , #{element.posicionSap} from dual
)

mapper.delete:
    DELETE FROM cer_historial_detalle_sap
	WHERE 1 = 1

		AND cer_historial_detalle_sap_id = #{id, jdbcType=INTEGER}


		AND cer_historial_id = #{cerHistorial.id}


		AND descripcion = #{descripcion}


		AND posicion_sap = #{posicionSap}
