mapper.select:
	SELECT 
      h.cer_historial_id ,
	  h.cer_certificado_id ,
 	  h.descripcion ,
      h.usuario_historal ,
      h.fecha_historial ,
      h.mtr_estado_id ,
      h.nro_documento_sap_asociado,
	  u.codigo_estado as codigoEstado,
      u.descripcion as descripcionEstado,
	  h.ind_sgmm
	FROM
	cer_historial h
	LEFT JOIN MTR_ESTADO u ON (u.mtr_estado_id = h.mtr_estado_id)
	WHERE 1 = 1

		AND h.cer_historial_id = #{id, jdbcType=INTEGER}


		AND h.cer_certificado_id = #{cerCertificado.id, jdbcType=INTEGER}


		AND h.descripcion = #{descripcion}


		AND h.usuario_historal = #{usuarioHistoral}


		AND h.fecha_historial = #{fechaHistorial, jdbcType=DATE}


		AND h.mtr_estado_id = #{mtrEstado.id, jdbcType=INTEGER}


		AND h.nro_documento_sap_asociado = #{nroDocumentoSapAsociado}

	order by h.fecha_historial

mapper.insert:
INSERT INTO cer_historial( cer_historial_id , cer_certificado_id , descripcion , usuario_historal , fecha_historial , mtr_estado_id , nro_documento_sap_asociado ) (
SELECT #{element.id} , #{element.cerCertificado.id} , #{element.descripcion} , #{element.usuarioHistoral} , #{element.fechaHistorial} , #{element.mtrEstado.id} , #{element.nroDocumentoSapAsociado} from dual
)

mapper.delete:
    DELETE FROM cer_historial
	WHERE 1 = 1

		AND cer_historial_id = #{id, jdbcType=INTEGER}


		AND cer_certificado_id = #{cerCertificado.id}


		AND descripcion = #{descripcion}


		AND usuario_historal = #{usuarioHistoral}


		AND fecha_historial = #{fechaHistorial, jdbcType=DATE}


		AND mtr_estado_id = #{mtrEstado.id}


		AND nro_documento_sap_asociado = #{nroDocumentoSapAsociado}
