mapper.select:
	SELECT 
      cer_nota_pedido_adjunto_id , 
      cer_nota_pedido_id , 
      carpeta_id , 
      archivo_id , 
      archivo_nombre , 
      archivo_tipo , 
      ruta_catalogo , 
      tipo_adjunto , 
      adjunto_aprobador , 
      size 
	FROM
	cer_nota_pedido_adjunto
	WHERE 1 = 1

		AND cer_nota_pedido_adjunto_id = #{id, jdbcType=INTEGER}


		AND cer_nota_pedido_id = #{cerNotaPedido.id, jdbcType=INTEGER}


		AND carpeta_id = #{carpetaId}


		AND archivo_id = #{archivoId}


		AND archivo_nombre = #{archivoNombre}


		AND archivo_tipo = #{archivoTipo}


		AND ruta_catalogo = #{rutaCatalogo}


		AND tipo_adjunto = #{tipoAdjunto}


		AND adjunto_aprobador = #{adjuntoAprobador}


		AND size = #{size, jdbcType=INTEGER}

mapper.insert:
INSERT INTO cer_nota_pedido_adjunto( cer_nota_pedido_adjunto_id , cer_nota_pedido_id , carpeta_id , archivo_id , archivo_nombre , archivo_tipo , ruta_catalogo , tipo_adjunto , adjunto_aprobador , size ) (
SELECT #{element.id} , #{element.cerNotaPedido.id} , #{element.carpetaId} , #{element.archivoId} , #{element.archivoNombre} , #{element.archivoTipo} , #{element.rutaCatalogo} , #{element.tipoAdjunto} , #{element.adjuntoAprobador} , #{element.size} from dual
)

mapper.delete:
    DELETE FROM cer_nota_pedido_adjunto
	WHERE 1 = 1

