		AND cer_nota_pedido_id = #{cerNotaPedido.id}


		AND carpeta_id = #{carpetaId}


		AND archivo_id = #{archivoId}


		AND archivo_nombre = #{archivoNombre}


		AND archivo_tipo = #{archivoTipo}


		AND ruta_catalogo = #{rutaCatalogo}


		AND tipo_adjunto = #{tipoAdjunto}


		AND adjunto_aprobador = #{adjuntoAprobador}


		AND size = #{size, jdbcType=INTEGER}
