mapper.select:
	SELECT 
      cer_nota_pedido_historial_id , 
      cer_nota_pedido_id , 
      descripcion , 
      usuario_historal , 
      fecha_historial , 
      mtr_estado_id 
	FROM
	cer_nota_pedido_historial
	WHERE 1 = 1

		AND cer_nota_pedido_historial_id = #{id, jdbcType=INTEGER}


		AND cer_nota_pedido_id = #{cerNotaPedido.id, jdbcType=INTEGER}


		AND descripcion = #{descripcion}


		AND usuario_historal = #{usuarioHistoral}


		AND fecha_historial = #{fechaHistorial, jdbcType=DATE}


		AND mtr_estado_id = #{mtrEstado.id, jdbcType=INTEGER}

mapper.insert:
INSERT INTO cer_nota_pedido_historial( cer_nota_pedido_historial_id , cer_nota_pedido_id , descripcion , usuario_historal , fecha_historial , mtr_estado_id ) (
SELECT #{element.id} , #{element.cerNotaPedido.id} , #{element.descripcion} , #{element.usuarioHistoral} , #{element.fechaHistorial} , #{element.mtrEstado.id} from dual
)

mapper.delete:
    DELETE FROM cer_nota_pedido_historial
	WHERE 1 = 1

		AND cer_nota_pedido_historial_id = #{id, jdbcType=INTEGER}


		AND cer_nota_pedido_id = #{cerNotaPedido.id}


		AND descripcion = #{descripcion}


		AND usuario_historal = #{usuarioHistoral}


		AND fecha_historial = #{fechaHistorial, jdbcType=DATE}


		AND mtr_estado_id = #{mtrEstado.id}
