mapper.select:
SELECT
cer_nota_pedido_id ,
codigo_nota_pedido_sap ,
mtr_sociedad_id ,
mtr_proveedor_id ,
mtr_condicion_pago_id ,
mtr_moneda_id ,
mtr_clase_documento_id ,
mtr_org_compra_id ,
mtr_estado_id ,
user_compra ,
tnotas_aclaratorias ,
tlugar_pago ,
tforma_pago ,
tdoc_particulares ,
tconcepto ,
plazo ,
nro_pedido_original ,
nro_bid ,
monto ,
fecha_inicio ,
fecha_fin ,
fecha_documento ,
fecha_cierre ,
fecha_acuse_recibo ,
descripcion ,
concepto ,
fecha_fin_vigencia ,
email_user_compra ,
tipo ,
nombre_completo_compra
FROM
cer_nota_pedido
WHERE 1 = 1

	AND cer_nota_pedido_id = #{id, jdbcType=INTEGER}


	AND codigo_nota_pedido_sap = #{codigoNotaPedidoSap}


	AND mtr_sociedad_id = #{mtrSociedad.id, jdbcType=INTEGER}


	AND mtr_proveedor_id = #{mtrProveedor.id, jdbcType=INTEGER}


	AND mtr_condicion_pago_id = #{mtrCondicionPago.id, jdbcType=INTEGER}


	AND mtr_moneda_id = #{mtrMoneda.id, jdbcType=INTEGER}


	AND mtr_clase_documento_id = #{mtrClaseDocumento.id, jdbcType=INTEGER}


	AND mtr_org_compra_id = #{mtrOrgCompra.id, jdbcType=INTEGER}


	AND mtr_estado_id = #{mtrEstado.id, jdbcType=INTEGER}


	AND user_compra = #{userCompra}


	AND tnotas_aclaratorias = #{tnotasAclaratorias}


	AND tlugar_pago = #{tlugarPago}


	AND tforma_pago = #{tformaPago}


	AND tdoc_particulares = #{tdocParticulares}


	AND tconcepto = #{tconcepto}


	AND plazo = #{plazo, jdbcType=INTEGER}


	AND nro_pedido_original = #{nroPedidoOriginal}


	AND nro_bid = #{nroBid}


	AND monto = #{monto, jdbcType=NUMERIC}


	AND fecha_inicio = #{fechaInicio, jdbcType=DATE}


	AND fecha_fin = #{fechaFin, jdbcType=DATE}


	AND fecha_documento = #{fechaDocumento, jdbcType=DATE}


	AND fecha_cierre = #{fechaCierre, jdbcType=DATE}


	AND fecha_acuse_recibo = #{fechaAcuseRecibo, jdbcType=DATE}


	AND descripcion = #{descripcion}


	AND concepto = #{concepto}


	AND fecha_fin_vigencia = #{fechaFinVigencia, jdbcType=DATE}


	AND email_user_compra = #{emailUserCompra}


	AND tipo = #{tipo}


	AND nombre_completo_compra = #{nombreCompletoCompra}

mapper.insert:
INSERT INTO cer_nota_pedido( cer_nota_pedido_id , codigo_nota_pedido_sap , mtr_sociedad_id , mtr_proveedor_id , mtr_condicion_pago_id , mtr_moneda_id , mtr_clase_documento_id , mtr_org_compra_id , mtr_estado_id , user_compra , tnotas_aclaratorias , tlugar_pago , tforma_pago , tdoc_particulares , tconcepto , plazo , nro_pedido_original , nro_bid , monto , fecha_inicio , fecha_fin , fecha_documento , fecha_cierre , fecha_acuse_recibo , descripcion , concepto , fecha_fin_vigencia , email_user_compra , tipo , nombre_completo_compra ) (
SELECT #{element.id} , #{element.codigoNotaPedidoSap} , #{element.mtrSociedad.id} , #{element.mtrProveedor.id} , #{element.mtrCondicionPago.id} , #{element.mtrMoneda.id} , #{element.mtrClaseDocumento.id} , #{element.mtrOrgCompra.id} , #{element.mtrEstado.id} , #{element.userCompra} , #{element.tnotasAclaratorias} , #{element.tlugarPago} , #{element.tformaPago} , #{element.tdocParticulares} , #{element.tconcepto} , #{element.plazo} , #{element.nroPedidoOriginal} , #{element.nroBid} , #{element.monto} , #{element.fechaInicio} , #{element.fechaFin} , #{element.fechaDocumento} , #{element.fechaCierre} , #{element.fechaAcuseRecibo} , #{element.descripcion} , #{element.concepto} , #{element.fechaFinVigencia} , #{element.emailUserCompra} , #{element.tipo} , #{element.nombreCompletoCompra} from dual
)

mapper.delete:
