mapper.select:
	SELECT 
      fac_estrategia_firma_id , 
      fac_estrategia_iteracion_id , 
      mtr_estrategia_facturacion_firma_id , 
      orden_ejecucion , 
      mtr_sector_id , 
      mtr_usuario_facturacion_id , 
      ind_aprobacion_rechazo , 
      mtr_tipo_gerencia_id , 
      activo 
	FROM
	fac_estrategia_firma
	WHERE 1 = 1

		AND fac_estrategia_firma_id = #{id, jdbcType=INTEGER}


		AND fac_estrategia_iteracion_id = #{facEstrategiaIteracion.id, jdbcType=INTEGER}


		AND mtr_estrategia_facturacion_firma_id = #{mtrEstrategiaFacturacionFirma.id, jdbcType=INTEGER}


		AND orden_ejecucion = #{ordenEjecucion, jdbcType=INTEGER}


		AND mtr_sector_id = #{mtrSector.id, jdbcType=INTEGER}


		AND mtr_usuario_facturacion_id = #{mtrUsuarioFacturacion.id, jdbcType=INTEGER}


		AND ind_aprobacion_rechazo = #{indAprobacionRechazo}


		AND mtr_tipo_gerencia_id = #{mtrTipoGerencia.id, jdbcType=INTEGER}


		AND activo = #{activo}

mapper.insert:
INSERT INTO fac_estrategia_firma( fac_estrategia_firma_id , fac_estrategia_iteracion_id , mtr_estrategia_facturacion_firma_id , orden_ejecucion , mtr_sector_id , mtr_usuario_facturacion_id , ind_aprobacion_rechazo , mtr_tipo_gerencia_id , activo ) (
SELECT #{element.id} , #{element.facEstrategiaIteracion.id} , #{element.mtrEstrategiaFacturacionFirma.id} , #{element.ordenEjecucion} , #{element.mtrSector.id} , #{element.mtrUsuarioFacturacion.id} , #{element.indAprobacionRechazo} , #{element.mtrTipoGerencia.id} , #{element.activo} from dual
)

mapper.delete:
    DELETE FROM fac_estrategia_firma
	WHERE 1 = 1

		AND fac_estrategia_firma_id = #{id, jdbcType=INTEGER}


		AND fac_estrategia_iteracion_id = #{facEstrategiaIteracion.id}


		AND mtr_estrategia_facturacion_firma_id = #{mtrEstrategiaFacturacionFirma.id}


		AND orden_ejecucion = #{ordenEjecucion, jdbcType=INTEGER}


		AND mtr_sector_id = #{mtrSector.id}


		AND mtr_usuario_facturacion_id = #{mtrUsuarioFacturacion.id}


		AND ind_aprobacion_rechazo = #{indAprobacionRechazo}


		AND mtr_tipo_gerencia_id = #{mtrTipoGerencia.id}


		AND activo = #{activo}
