mapper.select:
	SELECT 
      fac_estrategia_iteracion_id , 
      fac_factura_id , 
      nro_iteracion , 
      mtr_usuario_facturacion_cupa_id , 
      mtr_tipo_contrato_id , 
      mtr_estrategia_facturacion_id , 
      mtr_usuario_facturacion_aprobador0_id , 
      ind_aprobacion_rechazo_cupa , 
      ind_aprobacion_rechazo_aprobador0 , 
      ind_aprobacion_rechazo_firma , 
      nro_firmante_total , 
      nro_firmante_en_revision , 
      activo 
	FROM
	fac_estrategia_iteracion
	WHERE 1 = 1

		AND fac_estrategia_iteracion_id = #{id, jdbcType=INTEGER}


		AND fac_factura_id = #{facFactura.id, jdbcType=INTEGER}


		AND nro_iteracion = #{nroIteracion, jdbcType=INTEGER}


		AND mtr_usuario_facturacion_cupa_id = #{mtrUsuarioFacturacionCupa.id, jdbcType=INTEGER}


		AND mtr_tipo_contrato_id = #{mtrTipoContrato.id, jdbcType=INTEGER}


		AND mtr_estrategia_facturacion_id = #{mtrEstrategiaFacturacion.id, jdbcType=INTEGER}


		AND mtr_usuario_facturacion_aprobador0_id = #{mtrUsuarioFacturacionAprobador0.id, jdbcType=INTEGER}


		AND ind_aprobacion_rechazo_cupa = #{indAprobacionRechazoCupa}


		AND ind_aprobacion_rechazo_aprobador0 = #{indAprobacionRechazoAprobador0}


		AND ind_aprobacion_rechazo_firma = #{indAprobacionRechazoFirma}


		AND nro_firmante_total = #{nroFirmanteTotal, jdbcType=INTEGER}


		AND nro_firmante_en_revision = #{nroFirmanteEnRevision, jdbcType=INTEGER}


		AND activo = #{activo}

mapper.insert:
INSERT INTO fac_estrategia_iteracion( fac_estrategia_iteracion_id , fac_factura_id , nro_iteracion , mtr_usuario_facturacion_cupa_id , mtr_tipo_contrato_id , mtr_estrategia_facturacion_id , mtr_usuario_facturacion_aprobador0_id , ind_aprobacion_rechazo_cupa , ind_aprobacion_rechazo_aprobador0 , ind_aprobacion_rechazo_firma , nro_firmante_total , nro_firmante_en_revision , activo ) (
SELECT #{element.id} , #{element.facFactura.id} , #{element.nroIteracion} , #{element.mtrUsuarioFacturacionCupa.id} , #{element.mtrTipoContrato.id} , #{element.mtrEstrategiaFacturacion.id} , #{element.mtrUsuarioFacturacionAprobador0.id} , #{element.indAprobacionRechazoCupa} , #{element.indAprobacionRechazoAprobador0} , #{element.indAprobacionRechazoFirma} , #{element.nroFirmanteTotal} , #{element.nroFirmanteEnRevision} , #{element.activo} from dual
)

mapper.delete:
    DELETE FROM fac_estrategia_iteracion
	WHERE 1 = 1

		AND fac_estrategia_iteracion_id = #{id, jdbcType=INTEGER}


		AND fac_factura_id = #{facFactura.id}


		AND nro_iteracion = #{nroIteracion, jdbcType=INTEGER}


		AND mtr_usuario_facturacion_cupa_id = #{mtrUsuarioFacturacionCupa.id}


		AND mtr_tipo_contrato_id = #{mtrTipoContrato.id}


		AND mtr_estrategia_facturacion_id = #{mtrEstrategiaFacturacion.id}


		AND mtr_usuario_facturacion_aprobador0_id = #{mtrUsuarioFacturacionAprobador0.id}


		AND ind_aprobacion_rechazo_cupa = #{indAprobacionRechazoCupa}


		AND ind_aprobacion_rechazo_aprobador0 = #{indAprobacionRechazoAprobador0}


		AND ind_aprobacion_rechazo_firma = #{indAprobacionRechazoFirma}


		AND nro_firmante_total = #{nroFirmanteTotal, jdbcType=INTEGER}


		AND nro_firmante_en_revision = #{nroFirmanteEnRevision, jdbcType=INTEGER}


		AND activo = #{activo}
