mapper.select:
	SELECT 
      fac_factura_certificado_id , 
      fac_factura_id , 
      cer_certificado_id , 
      estado 
	FROM
	fac_factura_certificado
	WHERE 1 = 1

		AND fac_factura_certificado_id = #{id, jdbcType=INTEGER}


		AND fac_factura_id = #{facFactura.id, jdbcType=INTEGER}


		AND cer_certificado_id = #{cerCertificado.id, jdbcType=INTEGER}


		AND estado = #{estado}

mapper.insert:
INSERT INTO fac_factura_certificado( fac_factura_certificado_id , fac_factura_id , cer_certificado_id , estado ) (
SELECT #{element.id} , #{element.facFactura.id} , #{element.cerCertificado.id} , #{element.estado} from dual
)

mapper.delete:
    DELETE FROM fac_factura_certificado
	WHERE 1 = 1

		AND fac_factura_certificado_id = #{id, jdbcType=INTEGER}


		AND fac_factura_id = #{facFactura.id}


		AND cer_certificado_id = #{cerCertificado.id}


		AND estado = #{estado}
