mapper.select:
	SELECT 
      fac_factura_trama_sap_id , 
      fac_factura_id , 
      nombre_rfc , 
      texto_trama01 , 
      texto_trama02 , 
      texto_trama03 , 
      usuario_ejecutor 
	FROM
	fac_factura_trama_sap
	WHERE 1 = 1

		AND fac_factura_trama_sap_id = #{id, jdbcType=INTEGER}


		AND fac_factura_id = #{facFactura.id, jdbcType=INTEGER}


		AND nombre_rfc = #{nombreRfc}


		AND texto_trama01 = #{textoTrama01}


		AND texto_trama02 = #{textoTrama02}


		AND texto_trama03 = #{textoTrama03}


		AND usuario_ejecutor = #{usuarioEjecutor}

mapper.insert:
INSERT INTO fac_factura_trama_sap( fac_factura_trama_sap_id , fac_factura_id , nombre_rfc , texto_trama01 , texto_trama02 , texto_trama03 , usuario_ejecutor ) (
SELECT #{element.id} , #{element.facFactura.id} , #{element.nombreRfc} , #{element.textoTrama01} , #{element.textoTrama02} , #{element.textoTrama03} , #{element.usuarioEjecutor} from dual
)

mapper.delete:
    DELETE FROM fac_factura_trama_sap
	WHERE 1 = 1

		AND fac_factura_trama_sap_id = #{id, jdbcType=INTEGER}


		AND fac_factura_id = #{facFactura.id}


		AND nombre_rfc = #{nombreRfc}


		AND texto_trama01 = #{textoTrama01}


		AND texto_trama02 = #{textoTrama02}


		AND texto_trama03 = #{textoTrama03}


		AND usuario_ejecutor = #{usuarioEjecutor}
