mapper.select:
	SELECT 
      fac_historial_id , 
      fac_factura_id , 
      descripcion , 
      usuario_historial , 
      fecha_historial , 
      mtr_estado_id 
	FROM
	fac_historial
	WHERE 1 = 1

		AND fac_historial_id = #{id, jdbcType=INTEGER}


		AND fac_factura_id = #{facFactura.id, jdbcType=INTEGER}


		AND descripcion = #{descripcion}


		AND usuario_historial = #{usuarioHistorial}


		AND fecha_historial = #{fechaHistorial, jdbcType=DATE}


		AND mtr_estado_id = #{mtrEstado.id, jdbcType=INTEGER}

mapper.insert:
INSERT INTO fac_historial( fac_historial_id , fac_factura_id , descripcion , usuario_historial , fecha_historial , mtr_estado_id ) (
SELECT #{element.id} , #{element.facFactura.id} , #{element.descripcion} , #{element.usuarioHistorial} , #{element.fechaHistorial} , #{element.mtrEstado.id} from dual
)

mapper.delete:
    DELETE FROM fac_historial
	WHERE 1 = 1

		AND fac_historial_id = #{id, jdbcType=INTEGER}


		AND fac_factura_id = #{facFactura.id}


		AND descripcion = #{descripcion}


		AND usuario_historial = #{usuarioHistorial}


		AND fecha_historial = #{fechaHistorial, jdbcType=DATE}


		AND mtr_estado_id = #{mtrEstado.id}
