mapper.select:
	SELECT 
      fac_imputacion_id , 
      fac_factura_id , 
      mtr_cuenta_imputacion_id , 
      valor_imputacion , 
      estado 
	FROM
	fac_imputacion
	WHERE 1 = 1

		AND fac_imputacion_id = #{id, jdbcType=INTEGER}


		AND fac_factura_id = #{facFactura.id, jdbcType=INTEGER}


		AND mtr_cuenta_imputacion_id = #{mtrCuentaImputacion.id, jdbcType=INTEGER}


		AND valor_imputacion = #{valorImputacion, jdbcType=NUMERIC}


		AND estado = #{estado}

mapper.insert:
INSERT INTO fac_imputacion( fac_imputacion_id , fac_factura_id , mtr_cuenta_imputacion_id , valor_imputacion , estado ) (
SELECT #{element.id} , #{element.facFactura.id} , #{element.mtrCuentaImputacion.id} , #{element.valorImputacion} , #{element.estado} from dual
)

mapper.delete:
    DELETE FROM fac_imputacion
	WHERE 1 = 1

		AND fac_imputacion_id = #{id, jdbcType=INTEGER}


		AND fac_factura_id = #{facFactura.id}


		AND mtr_cuenta_imputacion_id = #{mtrCuentaImputacion.id}


		AND valor_imputacion = #{valorImputacion, jdbcType=NUMERIC}


		AND estado = #{estado}
