mapper.select:
	SELECT 
      fac_mensaje_sap_id , 
      fac_factura_id , 
      tipo_mensaje , 
      texto_mensaje_sap 
	FROM
	fac_mensaje_sap
	WHERE 1 = 1

		AND fac_mensaje_sap_id = #{id, jdbcType=INTEGER}


		AND fac_factura_id = #{facFactura.id, jdbcType=INTEGER}


		AND tipo_mensaje = #{tipoMensaje}


		AND texto_mensaje_sap = #{textoMensajeSap}

mapper.insert:
INSERT INTO fac_mensaje_sap( fac_mensaje_sap_id , fac_factura_id , tipo_mensaje , texto_mensaje_sap ) (
SELECT #{element.id} , #{element.facFactura.id} , #{element.tipoMensaje} , #{element.textoMensajeSap} from dual
)

mapper.delete:
    DELETE FROM fac_mensaje_sap
	WHERE 1 = 1

		AND fac_mensaje_sap_id = #{id, jdbcType=INTEGER}


		AND fac_factura_id = #{facFactura.id}


		AND tipo_mensaje = #{tipoMensaje}


		AND texto_mensaje_sap = #{textoMensajeSap}
