mapper.select:
	SELECT 
      fac_sustento_id , 
      fac_anio , 
      fac_correlativo 
	FROM
	fac_sustento
	WHERE 1 = 1

		AND fac_sustento_id = #{id, jdbcType=INTEGER}


		AND fac_anio = #{facAnio}


		AND fac_correlativo = #{facCorrelativo}

mapper.insert:
INSERT INTO fac_sustento( fac_sustento_id , fac_anio , fac_correlativo ) (
SELECT #{element.id} , #{element.facAnio} , #{element.facCorrelativo} from dual
)

mapper.delete:
    DELETE FROM fac_sustento
	WHERE 1 = 1

		AND fac_sustento_id = #{id, jdbcType=INTEGER}


		AND fac_anio = #{facAnio}


		AND fac_correlativo = #{facCorrelativo}
