mapper.select:
	SELECT 
      MATRICULA_ID , 
      HORARIO_ID , 
      PARTICIPANTE_ID , 
      CODIGO 
	FROM
	MATRICULA
	WHERE 1 = 1

		AND MATRICULA_ID = #{id, jdbcType=INTEGER}


		AND HORARIO_ID = #{horario.id, jdbcType=INTEGER}


		AND PARTICIPANTE_ID = #{participante.id, jdbcType=INTEGER}


		AND CODIGO = #{codigo}

mapper.insert:
INSERT INTO MATRICULA( MATRICULA_ID , HORARIO_ID , PARTICIPANTE_ID , CODIGO ) (
SELECT #{element.id} , #{element.horario.id} , #{element.participante.id} , #{element.codigo} from dual
)

mapper.delete:
    DELETE FROM MATRICULA
	WHERE 1 = 1

		AND MATRICULA_ID = #{id, jdbcType=INTEGER}


		AND HORARIO_ID = #{horario.id}


		AND PARTICIPANTE_ID = #{participante.id}


		AND CODIGO = #{codigo}
