mapper.select:
SELECT
mig_certificado_adjunto_id ,
mig_certificado_id ,
name ,
modified ,
identidad ,
nombre_archivo ,
usuario_firma ,
usuario_vendedor ,
item_type ,
scp_carpeta_id ,
scp_archivo_id ,
scp_archivo_nombre ,
scp_archivo_nombre_final ,
scp_archivo_tipo ,
scp_ruta_catalogo ,
scp_size ,
es_ok
FROM
mig_certificado_adjunto
WHERE 1 = 1

	AND mig_certificado_adjunto_id = #{id, jdbcType=INTEGER}


	AND mig_certificado_id = #{migCertificado.id, jdbcType=INTEGER}


	AND name = #{name}


	AND modified = #{modified}


	AND identidad = #{identidad}


	AND nombre_archivo = #{nombreArchivo}


	AND usuario_firma = #{usuarioFirma}


	AND usuario_vendedor = #{usuarioVendedor}


	AND item_type = #{itemType}


	AND scp_carpeta_id = #{scpCarpetaId}


	AND scp_archivo_id = #{scpArchivoId}


	AND scp_archivo_nombre = #{scpArchivoNombre}


	AND scp_archivo_nombre_final = #{scpArchivoNombreFinal}


	AND scp_archivo_tipo = #{scpArchivoTipo}


	AND scp_ruta_catalogo = #{scpRutaCatalogo}


	AND scp_size = #{scpSize, jdbcType=INTEGER}


	AND es_ok = #{esOk}

mapper.insert:
INSERT INTO mig_certificado_adjunto( mig_certificado_adjunto_id , mig_certificado_id , name , modified , identidad , nombre_archivo , usuario_firma , usuario_vendedor , item_type , scp_carpeta_id , scp_archivo_id , scp_archivo_nombre , scp_archivo_nombre_final , scp_archivo_tipo , scp_ruta_catalogo , scp_size , es_ok ) (
SELECT #{element.id} , #{element.migCertificado.id} , #{element.name} , #{element.modified} , #{element.identidad} , #{element.nombreArchivo} , #{element.usuarioFirma} , #{element.usuarioVendedor} , #{element.itemType} , #{element.scpCarpetaId} , #{element.scpArchivoId} , #{element.scpArchivoNombre} , #{element.scpArchivoNombreFinal} , #{element.scpArchivoTipo} , #{element.scpRutaCatalogo} , #{element.scpSize} , #{element.esOK} from dual
)

mapper.delete:
DELETE FROM mig_certificado_adjunto
WHERE 1 = 1

	AND mig_certificado_adjunto_id = #{id, jdbcType=INTEGER}


	AND mig_certificado_id = #{migCertificado.id}


	AND name = #{name}


	AND modified = #{modified}


	AND identidad = #{identidad}


	AND nombre_archivo = #{nombreArchivo}


	AND usuario_firma = #{usuarioFirma}


	AND usuario_vendedor = #{usuarioVendedor}


	AND item_type = #{itemType}


	AND scp_carpeta_id = #{scpCarpetaId}


	AND scp_archivo_id = #{scpArchivoId}


	AND scp_archivo_nombre = #{scpArchivoNombre}


	AND scp_archivo_nombre_final = #{scpArchivoNombreFinal}


	AND scp_archivo_tipo = #{scpArchivoTipo}


	AND scp_ruta_catalogo = #{scpRutaCatalogo}


	AND scp_size = #{scpSize, jdbcType=INTEGER}


	AND es_ok = #{esOK}
