mapper.select:
	SELECT 
      mig_certificado_detalle_id , 
      mig_certificado_id , 
      posicion , 
      moneda , 
      descripcion , 
      unidad_medida , 
      cuenta_contable , 
      cc_afe_orden , 
      precio_unitario , 
      cantidad_entregada , 
      total_linea , 
      hoja_servicio 
	FROM
	mig_certificado_detalle
	WHERE 1 = 1

		AND mig_certificado_detalle_id = #{id, jdbcType=INTEGER}


		AND mig_certificado_id = #{migCertificado.id, jdbcType=INTEGER}


		AND posicion = #{posicion, jdbcType=INTEGER}


		AND moneda = #{moneda}


		AND descripcion = #{descripcion}


		AND unidad_medida = #{unidadMedida}


		AND cuenta_contable = #{cuentaContable}


		AND cc_afe_orden = #{ccAfeOrden}


		AND precio_unitario = #{precioUnitario, jdbcType=NUMERIC}


		AND cantidad_entregada = #{cantidadEntregada, jdbcType=NUMERIC}


		AND total_linea = #{totalLinea, jdbcType=NUMERIC}


		AND hoja_servicio = #{hojaServicio}

mapper.insert:
INSERT INTO mig_certificado_detalle( mig_certificado_detalle_id , mig_certificado_id , posicion , moneda , descripcion , unidad_medida , cuenta_contable , cc_afe_orden , precio_unitario , cantidad_entregada , total_linea , hoja_servicio ) (
SELECT #{element.id} , #{element.migCertificado.id} , #{element.posicion} , #{element.moneda} , #{element.descripcion} , #{element.unidadMedida} , #{element.cuentaContable} , #{element.ccAfeOrden} , #{element.precioUnitario} , #{element.cantidadEntregada} , #{element.totalLinea} , #{element.hojaServicio} from dual
)

mapper.delete:
    DELETE FROM mig_certificado_detalle
	WHERE 1 = 1

		AND mig_certificado_detalle_id = #{id, jdbcType=INTEGER}


		AND mig_certificado_id = #{migCertificado.id}


		AND posicion = #{posicion, jdbcType=INTEGER}


		AND moneda = #{moneda}


		AND descripcion = #{descripcion}


		AND unidad_medida = #{unidadMedida}


		AND cuenta_contable = #{cuentaContable}


		AND cc_afe_orden = #{ccAfeOrden}


		AND precio_unitario = #{precioUnitario, jdbcType=NUMERIC}


		AND cantidad_entregada = #{cantidadEntregada, jdbcType=NUMERIC}


		AND total_linea = #{totalLinea, jdbcType=NUMERIC}


		AND hoja_servicio = #{hojaServicio}
