mapper.select:
	SELECT 
      mig_log_error_id , 
      tabla , 
      id_registro , 
      fecha_creacion , 
      error 
	FROM
	mig_log_error
	WHERE 1 = 1

		AND mig_log_error_id = #{id, jdbcType=INTEGER}


		AND tabla = #{tabla}


		AND id_registro = #{idRegistro, jdbcType=INTEGER}


		AND fecha_creacion = #{fechaCreacion, jdbcType=DATE}


		AND error = #{error}

mapper.insert:
INSERT INTO mig_log_error( mig_log_error_id , tabla , id_registro , fecha_creacion , error ) (
SELECT #{element.id} , #{element.tabla} , #{element.idRegistro} , #{element.fechaCreacion} , #{element.error} from dual
)

mapper.delete:
    DELETE FROM mig_log_error
	WHERE 1 = 1

		AND mig_log_error_id = #{id, jdbcType=INTEGER}


		AND tabla = #{tabla}


		AND id_registro = #{idRegistro, jdbcType=INTEGER}


		AND fecha_creacion = #{fechaCreacion, jdbcType=DATE}


		AND error = #{error}
