mapper.select:
	SELECT 
      mtr_aprobador_id , 
      aprobador_sap , 
      nombre , 
      apellidos , 
      email , 
      estado , 
      codigo_idp , 
      mtr_area_trabajo_id 
	FROM
	mtr_aprobador
	WHERE 1 = 1

		AND mtr_aprobador_id = #{id, jdbcType=INTEGER}


		AND aprobador_sap = #{aprobadorSap}


		AND nombre = #{nombre}


		AND apellidos = #{apellidos}


		AND email = #{email}


		AND estado = #{estado}


		AND codigo_idp = #{codigoIdp}


		AND mtr_area_trabajo_id = #{mtrAreaTrabajo.id, jdbcType=INTEGER}

mapper.insert:
INSERT INTO mtr_aprobador( mtr_aprobador_id , aprobador_sap , nombre , apellidos , email , estado , codigo_idp , mtr_area_trabajo_id ) (
SELECT #{element.id} , #{element.aprobadorSap} , #{element.nombre} , #{element.apellidos} , #{element.email} , #{element.estado} , #{element.codigoIdp} , #{element.mtrAreaTrabajo.id} from dual
)

mapper.delete:
    DELETE FROM mtr_aprobador
	WHERE 1 = 1

		AND mtr_aprobador_id = #{id, jdbcType=INTEGER}


		AND aprobador_sap = #{aprobadorSap}


		AND nombre = #{nombre}


		AND apellidos = #{apellidos}


		AND email = #{email}


		AND estado = #{estado}


		AND codigo_idp = #{codigoIdp}


		AND mtr_area_trabajo_id = #{mtrAreaTrabajo.id}
