mapper.select:
	SELECT 
      mtr_area_trabajo_id , 
      codigo_area_trabajo , 
      descripcion 
	FROM
	mtr_area_trabajo
	WHERE 1 = 1

		AND mtr_area_trabajo_id = #{id, jdbcType=INTEGER}


		AND codigo_area_trabajo = #{codigoAreaTrabajo}


		AND descripcion = #{descripcion}

mapper.insert:
INSERT INTO mtr_area_trabajo( mtr_area_trabajo_id , codigo_area_trabajo , descripcion ) (
SELECT #{element.id} , #{element.codigoAreaTrabajo} , #{element.descripcion} from dual
)

mapper.delete:
    DELETE FROM mtr_area_trabajo
	WHERE 1 = 1

		AND mtr_area_trabajo_id = #{id, jdbcType=INTEGER}


		AND codigo_area_trabajo = #{codigoAreaTrabajo}


		AND descripcion = #{descripcion}
