mapper.select:
SELECT
mtr_bien_servicio_id ,
codigo_sap ,
mtr_grupo_articulo_id ,
mtr_unidad_medida_id ,
descripcion ,
tipo_item ,
descripcion_breve
FROM
mtr_bien_servicio
WHERE 1 = 1

	AND mtr_bien_servicio_id = #{id, jdbcType=INTEGER}


	AND codigo_sap = #{codigoSap}


	AND mtr_grupo_articulo_id = #{mtrGrupoArticulo.id, jdbcType=INTEGER}


	AND mtr_unidad_medida_id = #{mtrUnidadMedida.id, jdbcType=INTEGER}


	AND descripcion = #{descripcion}


	AND tipo_item = #{tipoItem}


	AND descripcion_breve = #{descripcionBreve}

mapper.insert:
INSERT INTO mtr_bien_servicio( mtr_bien_servicio_id , codigo_sap , mtr_grupo_articulo_id , mtr_unidad_medida_id , descripcion , tipo_item , descripcion_breve ) (
SELECT #{element.id} , #{element.codigoSap} , #{element.mtrGrupoArticulo.id} , #{element.mtrUnidadMedida.id} , #{element.descripcion} , #{element.tipoItem} , #{element.descripcionBreve} from dual
)

mapper.delete:
DELETE FROM mtr_bien_servicio
WHERE 1 = 1

	AND mtr_bien_servicio_id = #{id, jdbcType=INTEGER}


	AND codigo_sap = #{codigoSap}


	AND mtr_grupo_articulo_id = #{mtrGrupoArticulo.id}


	AND mtr_unidad_medida_id = #{mtrUnidadMedida.id}


	AND descripcion = #{descripcion}


	AND tipo_item = #{tipoItem}


	AND descripcion_breve = #{descripcionBreve}
