mapper.select:
SELECT
mtr_centro_id ,
codigo_centro_sap ,
descripcion ,
id_padre ,
mtr_sociedad_id
FROM
mtr_centro_almacen
WHERE 1 = 1

	AND mtr_centro_id = #{id, jdbcType=INTEGER}


	AND codigo_centro_sap = #{codigoCentroSap}


	AND descripcion = #{descripcion}


	AND id_padre = #{idPadre, jdbcType=INTEGER}


	AND mtr_sociedad_id = #{mtrSociedad.id, jdbcType=INTEGER}

SELECT mtr_centro_id , codigo_centro_sap , descripcion , id_padre , mtr_sociedad_id FROM mtr_centro_almacen WHERE 1 = 1
AND codigo_centro_sap = #{codeSap}
AND id_padre = #{idPadre, jdbcType=INTEGER}
AND mtr_sociedad_id = #{sociedad, jdbcType=INTEGER}

mapper.insert:
INSERT INTO mtr_centro_almacen( mtr_centro_id , codigo_centro_sap , descripcion , id_padre , mtr_sociedad_id ) (
SELECT #{element.id} , #{element.codigoCentroSap} , #{element.descripcion} , #{element.idPadre} , #{element.mtrSociedad.id} from dual
)

mapper.delete:
DELETE FROM mtr_centro_almacen
WHERE 1 = 1

	AND mtr_centro_id = #{id, jdbcType=INTEGER}


	AND codigo_centro_sap = #{codigoCentroSap}


	AND descripcion = #{descripcion}


	AND id_padre = #{idPadre, jdbcType=INTEGER}


	AND mtr_sociedad_id = #{mtrSociedad.id}
