mapper.select:
SELECT
mtr_clase_documento_id ,
codigo_sap ,
descripcion ,
estado
FROM
mtr_clase_documento
WHERE 1 = 1

	AND mtr_clase_documento_id = #{id, jdbcType=INTEGER}


	AND codigo_sap = #{codigoSap}


	AND descripcion = #{descripcion}


	AND estado = #{estado}

mapper.insert:
INSERT INTO mtr_clase_documento( mtr_clase_documento_id , codigo_sap , descripcion , estado ) (
SELECT #{element.id} , #{element.codigoSap} , #{element.descripcion} , #{element.estado} from dual
)

mapper.delete:
DELETE FROM mtr_clase_documento
WHERE 1 = 1

	AND mtr_clase_documento_id = #{id, jdbcType=INTEGER}


	AND codigo_sap = #{codigoSap}


	AND descripcion = #{descripcion}


	AND estado = #{estado}
