mapper.select:
	SELECT 
      mtr_cuenta_imputacion_id , 
      codigo_cuenta_imputacion , 
      descripcion , 
      mtr_tipo_imputacion_id , 
      mtr_sociedad_id , 
      estatus 
	FROM
	mtr_cuenta_imputacion
	WHERE 1 = 1

		AND mtr_cuenta_imputacion_id = #{id, jdbcType=INTEGER}


		AND codigo_cuenta_imputacion = #{codigoCuentaImputacion}


		AND descripcion = #{descripcion}


		AND mtr_tipo_imputacion_id = #{mtrTipoImputacion.id, jdbcType=INTEGER}


		AND mtr_sociedad_id = #{mtrSociedad.id, jdbcType=INTEGER}


		AND estatus = #{estatus}

mapper.insert:
INSERT INTO mtr_cuenta_imputacion( mtr_cuenta_imputacion_id , codigo_cuenta_imputacion , descripcion , mtr_tipo_imputacion_id , mtr_sociedad_id , estatus ) (
SELECT #{element.id} , #{element.codigoCuentaImputacion} , #{element.descripcion} , #{element.mtrTipoImputacion.id} , #{element.mtrSociedad.id} , #{element.estatus} from dual
)

mapper.delete:
    DELETE FROM mtr_cuenta_imputacion
	WHERE 1 = 1

		AND mtr_cuenta_imputacion_id = #{id, jdbcType=INTEGER}


		AND codigo_cuenta_imputacion = #{codigoCuentaImputacion}


		AND descripcion = #{descripcion}


		AND mtr_tipo_imputacion_id = #{mtrTipoImputacion.id}


		AND mtr_sociedad_id = #{mtrSociedad.id}


		AND estatus = #{estatus}
