mapper.select:
	SELECT 
      mtr_detraccion_id , 
      codigo , 
      descripcion , 
      estado 
	FROM
	mtr_detraccion
	WHERE 1 = 1

		AND mtr_detraccion_id = #{id, jdbcType=INTEGER}


		AND codigo = #{codigo}


		AND descripcion = #{descripcion}


		AND estado = #{estado}

mapper.insert:
INSERT INTO mtr_detraccion( mtr_detraccion_id , codigo , descripcion , estado ) (
SELECT #{element.id} , #{element.codigo} , #{element.descripcion} , #{element.estado} from dual
)

mapper.delete:
    DELETE FROM mtr_detraccion
	WHERE 1 = 1

		AND mtr_detraccion_id = #{id, jdbcType=INTEGER}


		AND codigo = #{codigo}


		AND descripcion = #{descripcion}


		AND estado = #{estado}
