mapper.select:
SELECT
mtr_estado_id ,
codigo_agrupado ,
codigo_estado ,
descripcion
FROM
mtr_estado
WHERE 1 = 1

	AND mtr_estado_id = #{id, jdbcType=INTEGER}


	AND codigo_agrupado = #{codigoAgrupado}


	AND codigo_estado = #{codigoEstado}


	AND descripcion = #{descripcion}

mapper.insert:
INSERT INTO mtr_estado( mtr_estado_id , codigo_agrupado , codigo_estado , descripcion ) (
SELECT #{element.id} , #{element.codigoAgrupado} , #{element.codigoEstado} , #{element.descripcion} from dual
)

mapper.delete:
DELETE FROM mtr_estado
WHERE 1 = 1

	AND mtr_estado_id = #{id, jdbcType=INTEGER}


	AND codigo_agrupado = #{codigoAgrupado}


	AND codigo_estado = #{codigoEstado}


	AND descripcion = #{descripcion}
