mapper.select:
	SELECT 
      mtr_estrategia_facturacion_firma_id , 
      mtr_estrategia_facturacion_id , 
      orden_ejecucion , 
      mtr_sector_id , 
      mtr_tipo_gerencia_id 
	FROM
	mtr_estrategia_facturacion_firma
	WHERE 1 = 1

		AND mtr_estrategia_facturacion_firma_id = #{id, jdbcType=INTEGER}


		AND mtr_estrategia_facturacion_id = #{mtrEstrategiaFacturacion.id, jdbcType=INTEGER}


		AND orden_ejecucion = #{ordenEjecucion, jdbcType=INTEGER}


		AND mtr_sector_id = #{mtrSector.id, jdbcType=INTEGER}


		AND mtr_tipo_gerencia_id = #{mtrTipoGerencia.id, jdbcType=INTEGER}

mapper.insert:
INSERT INTO mtr_estrategia_facturacion_firma( mtr_estrategia_facturacion_firma_id , mtr_estrategia_facturacion_id , orden_ejecucion , mtr_sector_id , mtr_tipo_gerencia_id ) (
SELECT #{element.id} , #{element.mtrEstrategiaFacturacion.id} , #{element.ordenEjecucion} , #{element.mtrSector.id} , #{element.mtrTipoGerencia.id} from dual
)

mapper.delete:
    DELETE FROM mtr_estrategia_facturacion_firma
	WHERE 1 = 1

		AND mtr_estrategia_facturacion_firma_id = #{id, jdbcType=INTEGER}


		AND mtr_estrategia_facturacion_id = #{mtrEstrategiaFacturacion.id}


		AND orden_ejecucion = #{ordenEjecucion, jdbcType=INTEGER}


		AND mtr_sector_id = #{mtrSector.id}


		AND mtr_tipo_gerencia_id = #{mtrTipoGerencia.id}
