mapper.select:
	SELECT 
      mtr_estrategia_facturacion_id , 
      mtr_sociedad_id , 
      mtr_tipo_contrato_id , 
      mtr_rango_monto_estrategia_id , 
      mtr_sector_id 
	FROM
	mtr_estrategia_facturacion
	WHERE 1 = 1

		AND mtr_estrategia_facturacion_id = #{id, jdbcType=INTEGER}


		AND mtr_sociedad_id = #{mtrSociedad.id, jdbcType=INTEGER}


		AND mtr_tipo_contrato_id = #{mtrTipoContrato.id, jdbcType=INTEGER}


		AND mtr_rango_monto_estrategia_id = #{mtrRangoMontoEstrategia.id, jdbcType=INTEGER}


		AND mtr_sector_id = #{mtrSector.id, jdbcType=INTEGER}

mapper.insert:
INSERT INTO mtr_estrategia_facturacion( mtr_estrategia_facturacion_id , mtr_sociedad_id , mtr_tipo_contrato_id , mtr_rango_monto_estrategia_id , mtr_sector_id ) (
SELECT #{element.id} , #{element.mtrSociedad.id} , #{element.mtrTipoContrato.id} , #{element.mtrRangoMontoEstrategia.id} , #{element.mtrSector.id} from dual
)

mapper.delete:
    DELETE FROM mtr_estrategia_facturacion
	WHERE 1 = 1

		AND mtr_estrategia_facturacion_id = #{id, jdbcType=INTEGER}


		AND mtr_sociedad_id = #{mtrSociedad.id}


		AND mtr_tipo_contrato_id = #{mtrTipoContrato.id}


		AND mtr_rango_monto_estrategia_id = #{mtrRangoMontoEstrategia.id}


		AND mtr_sector_id = #{mtrSector.id}
