mapper.select:
	SELECT 
      mtr_historial_apagar_id , 
      fecha_historial , 
      usuario_historial , 
      estado_apagar 
	FROM
	mtr_historial_apagar
	WHERE 1 = 1

		AND mtr_historial_apagar_id = #{id, jdbcType=INTEGER}


		AND fecha_historial = #{fechaHistorial, jdbcType=DATE}


		AND usuario_historial = #{usuarioHistorial}


		AND estado_apagar = #{estadoApagar}

mapper.insert:
INSERT INTO mtr_historial_apagar( mtr_historial_apagar_id , fecha_historial , usuario_historial , estado_apagar ) (
SELECT #{element.id} , #{element.fechaHistorial} , #{element.usuarioHistorial} , #{element.estadoApagar} from dual
)

mapper.delete:
    DELETE FROM mtr_historial_apagar
	WHERE 1 = 1

		AND mtr_historial_apagar_id = #{id, jdbcType=INTEGER}


		AND fecha_historial = #{fechaHistorial, jdbcType=DATE}


		AND usuario_historial = #{usuarioHistorial}


		AND estado_apagar = #{estadoApagar}
