mapper.select:
	SELECT 
      mtr_informacion_noticia_id , 
      archivo_id , 
      archivo_nombre , 
      contenido , 
      fecha_caducidad , 
      fecha_creacion , 
      fecha_modificacion , 
      fecha_publicacion , 
      icon_text , 
      ind_activo , 
      ind_noticia_nuevo_proveedor , 
      ruta_adjunto , 
      texto_info , 
      titulo , 
      usuario_creacion , 
      usuario_modificacion , 
      mtr_tipo_informacion_noticia_id 
	FROM
	mtr_informacion_noticia
	WHERE 1 = 1

		AND mtr_informacion_noticia_id = #{id, jdbcType=INTEGER}


		AND archivo_id = #{archivoId}


		AND archivo_nombre = #{archivoNombre}


		AND contenido = #{contenido}


		AND fecha_caducidad = #{fechaCaducidad, jdbcType=DATE}


		AND fecha_creacion = #{fechaCreacion, jdbcType=DATE}


		AND fecha_modificacion = #{fechaModificacion, jdbcType=DATE}


		AND fecha_publicacion = #{fechaPublicacion, jdbcType=DATE}


		AND icon_text = #{iconText}


		AND ind_activo = #{indActivo}


		AND ind_noticia_nuevo_proveedor = #{indNoticiaNuevoProveedor}


		AND ruta_adjunto = #{rutaAdjunto}


		AND texto_info = #{textoInfo}


		AND titulo = #{titulo}


		AND usuario_creacion = #{usuarioCreacion, jdbcType=INTEGER}


		AND usuario_modificacion = #{usuarioModificacion, jdbcType=INTEGER}


		AND mtr_tipo_informacion_noticia_id = #{mtrTipoInformacionNoticia.id, jdbcType=INTEGER}

mapper.insert:
INSERT INTO mtr_informacion_noticia( mtr_informacion_noticia_id , archivo_id , archivo_nombre , contenido , fecha_caducidad , fecha_creacion , fecha_modificacion , fecha_publicacion , icon_text , ind_activo , ind_noticia_nuevo_proveedor , ruta_adjunto , texto_info , titulo , usuario_creacion , usuario_modificacion , mtr_tipo_informacion_noticia_id ) (
SELECT #{element.id} , #{element.archivoId} , #{element.archivoNombre} , #{element.contenido} , #{element.fechaCaducidad} , #{element.fechaCreacion} , #{element.fechaModificacion} , #{element.fechaPublicacion} , #{element.iconText} , #{element.indActivo} , #{element.indNoticiaNuevoProveedor} , #{element.rutaAdjunto} , #{element.textoInfo} , #{element.titulo} , #{element.usuarioCreacion} , #{element.usuarioModificacion} , #{element.mtrTipoInformacionNoticia.id} from dual
)

mapper.delete:
    DELETE FROM mtr_informacion_noticia
	WHERE 1 = 1

		AND mtr_informacion_noticia_id = #{id, jdbcType=INTEGER}


		AND archivo_id = #{archivoId}


		AND archivo_nombre = #{archivoNombre}


		AND contenido = #{contenido}


		AND fecha_caducidad = #{fechaCaducidad, jdbcType=DATE}


		AND fecha_creacion = #{fechaCreacion, jdbcType=DATE}


		AND fecha_modificacion = #{fechaModificacion, jdbcType=DATE}


		AND fecha_publicacion = #{fechaPublicacion, jdbcType=DATE}


		AND icon_text = #{iconText}


		AND ind_activo = #{indActivo}


		AND ind_noticia_nuevo_proveedor = #{indNoticiaNuevoProveedor}


		AND ruta_adjunto = #{rutaAdjunto}


		AND texto_info = #{textoInfo}


		AND titulo = #{titulo}


		AND usuario_creacion = #{usuarioCreacion, jdbcType=INTEGER}


		AND usuario_modificacion = #{usuarioModificacion, jdbcType=INTEGER}


		AND mtr_tipo_informacion_noticia_id = #{mtrTipoInformacionNoticia.id}
