mapper.select:
SELECT
mtr_moneda_id ,
sigla ,
moneda_descrip ,
desc_brv
FROM
mtr_moneda
WHERE 1 = 1

	AND mtr_moneda_id = #{id, jdbcType=INTEGER}


	AND sigla = #{sigla}


	AND moneda_descrip = #{monedaDescrip}


	AND desc_brv = #{descBrv}

mapper.insert:
INSERT INTO mtr_moneda( mtr_moneda_id , sigla , moneda_descrip , desc_brv ) (
SELECT #{element.id} , #{element.sigla} , #{element.monedaDescrip} , #{element.descBrv} from dual
)

mapper.delete:
DELETE FROM mtr_moneda
WHERE 1 = 1

	AND mtr_moneda_id = #{id, jdbcType=INTEGER}


	AND sigla = #{sigla}


	AND moneda_descrip = #{monedaDescrip}


	AND desc_brv = #{descBrv}
