mapper.select:
	SELECT 
      mtr_org_compra_id , 
      codigo_org_compra_sap , 
      descripcion , 
      estado 
	FROM
	mtr_org_compra
	WHERE 1 = 1

		AND mtr_org_compra_id = #{id, jdbcType=INTEGER}


		AND codigo_org_compra_sap = #{codigoOrgCompraSap}


		AND descripcion = #{descripcion}


		AND estado = #{estado}

mapper.insert:
INSERT INTO mtr_org_compra( mtr_org_compra_id , codigo_org_compra_sap , descripcion , estado ) (
SELECT #{element.id} , #{element.codigoOrgCompraSap} , #{element.descripcion} , #{element.estado} from dual
)

mapper.delete:
    DELETE FROM mtr_org_compra
	WHERE 1 = 1

		AND mtr_org_compra_id = #{id, jdbcType=INTEGER}


		AND codigo_org_compra_sap = #{codigoOrgCompraSap}


		AND descripcion = #{descripcion}


		AND estado = #{estado}
