mapper.select:
	SELECT 
      mtr_rango_monto_estrategia_id , 
      descripcion , 
      mtr_moneda_id , 
      valor_minimo , 
      valor_maximo 
	FROM
	mtr_rango_monto_estrategia
	WHERE 1 = 1

		AND mtr_rango_monto_estrategia_id = #{id, jdbcType=INTEGER}


		AND descripcion = #{descripcion}


		AND mtr_moneda_id = #{mtrMoneda.id, jdbcType=INTEGER}


		AND valor_minimo = #{valorMinimo, jdbcType=INTEGER}


		AND valor_maximo = #{valorMaximo, jdbcType=INTEGER}

mapper.insert:
INSERT INTO mtr_rango_monto_estrategia( mtr_rango_monto_estrategia_id , descripcion , mtr_moneda_id , valor_minimo , valor_maximo ) (
SELECT #{element.id} , #{element.descripcion} , #{element.mtrMoneda.id} , #{element.valorMinimo} , #{element.valorMaximo} from dual
)

mapper.delete:
    DELETE FROM mtr_rango_monto_estrategia
	WHERE 1 = 1

		AND mtr_rango_monto_estrategia_id = #{id, jdbcType=INTEGER}


		AND descripcion = #{descripcion}


		AND mtr_moneda_id = #{mtrMoneda.id}


		AND valor_minimo = #{valorMinimo, jdbcType=INTEGER}


		AND valor_maximo = #{valorMaximo, jdbcType=INTEGER}
