mapper.select:
	SELECT 
      MTR_REEMPLAZO_APROBADOR_ID , 
      FECHA_INICIO , 
      FECHA_FIN , 
      MTR_APROBADOR_ID , 
      MTR_REEMPLAZO_ID 
	FROM
	MTR_REEMPLAZO_APROBADOR
	WHERE 1 = 1

		AND MTR_REEMPLAZO_APROBADOR_ID = #{id, jdbcType=INTEGER}


		AND FECHA_INICIO = #{fechaInicio, jdbcType=DATE}


		AND FECHA_FIN = #{fechaFin, jdbcType=DATE}


		AND MTR_APROBADOR_ID = #{mtrAprobador.id, jdbcType=INTEGER}


		AND MTR_REEMPLAZO_ID = #{mtrReemplazo.id, jdbcType=INTEGER}

mapper.insert:
INSERT INTO MTR_REEMPLAZO_APROBADOR( MTR_REEMPLAZO_APROBADOR_ID , FECHA_INICIO , FECHA_FIN , MTR_APROBADOR_ID , MTR_REEMPLAZO_ID ) (
SELECT #{element.id} , #{element.fechaInicio} , #{element.fechaFin} , #{element.mtrAprobador.id} , #{element.mtrReemplazo.id} from dual
)

mapper.delete:
    DELETE FROM MTR_REEMPLAZO_APROBADOR
	WHERE 1 = 1

		AND MTR_REEMPLAZO_APROBADOR_ID = #{id, jdbcType=INTEGER}


		AND FECHA_INICIO = #{fechaInicio, jdbcType=DATE}


		AND FECHA_FIN = #{fechaFin, jdbcType=DATE}


		AND MTR_APROBADOR_ID = #{mtrAprobador.id}


		AND MTR_REEMPLAZO_ID = #{mtrReemplazo.id}
