mapper.select:
	SELECT 
      mtr_sector_id , 
      codigo_sector , 
      descripcion , 
      mtr_sociedad_id 
	FROM
	mtr_sector
	WHERE 1 = 1

		AND mtr_sector_id = #{id, jdbcType=INTEGER}


		AND codigo_sector = #{codigoSector}


		AND descripcion = #{descripcion}


		AND mtr_sociedad_id = #{mtrSociedad.id, jdbcType=INTEGER}

mapper.insert:
INSERT INTO mtr_sector( mtr_sector_id , codigo_sector , descripcion , mtr_sociedad_id ) (
SELECT #{element.id} , #{element.codigoSector} , #{element.descripcion} , #{element.mtrSociedad.id} from dual
)

mapper.delete:
    DELETE FROM mtr_sector
	WHERE 1 = 1

		AND mtr_sector_id = #{id, jdbcType=INTEGER}


		AND codigo_sector = #{codigoSector}


		AND descripcion = #{descripcion}


		AND mtr_sociedad_id = #{mtrSociedad.id}
