mapper.select:
SELECT
mtr_sociedad_id ,
desc_brv ,
descripcion ,
estado
FROM
mtr_sociedad
WHERE 1 = 1

	AND mtr_sociedad_id = #{id, jdbcType=INTEGER}


	AND desc_brv = #{descBrv}


	AND descripcion = #{descripcion}


	AND estado = #{estado}

mapper.insert:
INSERT INTO mtr_sociedad( mtr_sociedad_id , desc_brv , descripcion , estado ) (
SELECT #{element.id} , #{element.descBrv} , #{element.descripcion} , #{element.estado} from dual
)

mapper.delete:
DELETE FROM mtr_sociedad
WHERE 1 = 1

	AND mtr_sociedad_id = #{id, jdbcType=INTEGER}


	AND desc_brv = #{descBrv}


	AND descripcion = #{descripcion}


	AND estado = #{estado}
