mapper.select:
	SELECT 
      mtr_tasa_cambio_id , 
      fecha_tasa , 
      mtr_moneda_origen_id , 
      mtr_moneda_destino_id , 
      valor 
	FROM
	mtr_tasa_cambio
	WHERE 1 = 1

		AND mtr_tasa_cambio_id = #{id, jdbcType=INTEGER}


		AND fecha_tasa = #{fechaTasa, jdbcType=DATE}


		AND mtr_moneda_origen_id = #{mtrMonedaOrigen.id, jdbcType=INTEGER}


		AND mtr_moneda_destino_id = #{mtrMonedaDestino.id, jdbcType=INTEGER}


		AND valor = #{valor, jdbcType=NUMERIC}

mapper.insert:
INSERT INTO mtr_tasa_cambio( mtr_tasa_cambio_id , fecha_tasa , mtr_moneda_origen_id , mtr_moneda_destino_id , valor ) (
SELECT #{element.id} , #{element.fechaTasa} , #{element.mtrMonedaOrigen.id} , #{element.mtrMonedaDestino.id} , #{element.valor} from dual
)

mapper.delete:
    DELETE FROM mtr_tasa_cambio
	WHERE 1 = 1

		AND mtr_tasa_cambio_id = #{id, jdbcType=INTEGER}


		AND fecha_tasa = #{fechaTasa, jdbcType=DATE}


		AND mtr_moneda_origen_id = #{mtrMonedaOrigen.id}


		AND mtr_moneda_destino_id = #{mtrMonedaDestino.id}


		AND valor = #{valor, jdbcType=NUMERIC}
