mapper.select:
	SELECT 
      mtr_tasa_cambio_web_id , 
      fecha_tasa , 
      mtr_moneda_origen_id , 
      mtr_moneda_destino_id , 
      valor 
	FROM
	mtr_tasa_cambio_web
	WHERE 1 = 1

		AND mtr_tasa_cambio_web_id = #{id, jdbcType=INTEGER}


		AND fecha_tasa = #{fechaTasa, jdbcType=DATE}


		AND mtr_moneda_origen_id = #{mtrMonedaOrigenId, jdbcType=INTEGER}


		AND mtr_moneda_destino_id = #{mtrMonedaDestinoId, jdbcType=INTEGER}


		AND valor = #{valor, jdbcType=NUMERIC}

mapper.insert:
INSERT INTO mtr_tasa_cambio_web( mtr_tasa_cambio_web_id , fecha_tasa , mtr_moneda_origen_id , mtr_moneda_destino_id , valor ) (
SELECT #{element.id} , #{element.fechaTasa} , #{element.mtrMonedaOrigenId} , #{element.mtrMonedaDestinoId} , #{element.valor} from dual
)

mapper.delete:
    DELETE FROM mtr_tasa_cambio_web
	WHERE 1 = 1

		AND mtr_tasa_cambio_web_id = #{id, jdbcType=INTEGER}


		AND fecha_tasa = #{fechaTasa, jdbcType=DATE}


		AND mtr_moneda_origen_id = #{mtrMonedaOrigenId, jdbcType=INTEGER}


		AND mtr_moneda_destino_id = #{mtrMonedaDestinoId, jdbcType=INTEGER}


		AND valor = #{valor, jdbcType=NUMERIC}
