mapper.select:
SELECT mtr_tipo_contrato_id , descripcion FROM mtr_tipo_contrato WHERE 1 = 1
AND mtr_tipo_contrato_id = #{id, jdbcType=INTEGER}
AND descripcion = #{descripcion}

mapper.insert:
INSERT INTO  mtr_tipo_contrato(
mtr_tipo_contrato_id , 
descripcion 
)
VALUES
(
#{id} , 
#{descripcion} 
)

INSERT INTO  mtr_tipo_contrato(
mtr_tipo_contrato_id , 
descripcion 
)
(

SELECT
#{element.id} , 
#{element.descripcion} 
from dual


)

mapper.update:
UPDATE mtr_tipo_contrato
descripcion = #{descripcion},
WHERE mtr_tipo_contrato_id = #{id, jdbcType=INTEGER}

mapper.delete:
    DELETE FROM mtr_tipo_contrato
	WHERE 1 = 1

		AND mtr_tipo_contrato_id = #{id, jdbcType=INTEGER}


		AND descripcion = #{descripcion}
